package qangan.heapsort;

import static org.junit.jupiter.api.Assertions.*;

import org.junit.jupiter.api.Test;

import java.util.Arrays;
import java.util.Random;

class MainTest {
    @Test
    void testEmptyArray() {
        int[] arr = new int[0];
        Main.heapSort(arr);
        assertArrayEquals(new int[0], arr);
    }

    @Test
    void testSingleElement() {
        int[] arr = {2305};
        Main.heapSort(arr);
        assertArrayEquals(new int[] {2305}, arr);
    }

    @Test
    void testAllEqual() {
        int[] arr = {7, 7, 7, 7, 7};
        int[] expected = arr.clone();
        Main.heapSort(arr);
        assertArrayEquals(expected, arr);
    }

    @Test
    void testAlreadySorted() {
        int[] arr = {1, 2, 3, 4, 5, 6, 7};
        int[] expected = arr.clone();
        Main.heapSort(arr);
        assertArrayEquals(expected, arr);
    }

    @Test
    void testReverseSorted() {
        int[] arr = {9, 8, 7, 6, 5, 4, 3, 2, 1};
        int[] expected = arr.clone();
        Arrays.sort(expected);
        Main.heapSort(arr);
        assertArrayEquals(expected, arr);
    }

    @Test
    void RandomArrays() {
        Random random = new Random();
        for (int i = 0; i < 1000; i++) {
            int lenght = random.nextInt(1000);
            int[] arr = new int[lenght];
            for (int j = 0; j < lenght; j++) {
                arr[j] = random.nextInt(1000);
            }
            int[] cprarray = arr.clone();
            Main.heapSort(arr);
            Arrays.sort(cprarray);
            assertArrayEquals(arr, cprarray, "HeapSort made a bad work");
        }
    }

    @Test
    void TestMain() {
        assertNull(main());
    }
}
